import com.rahim.kalimaliev_rahim_homework5.databinding.FragmentFirstBinding


class FirstFragment : Fragment() {

private lateinit var binding: FragmentFirstBinding

companion object{
    const val USER = "USERNAME"
    const val STATUS = "STATUS"
    const val IMAGE = "IMAGE"
}

private var list =  arrayListOf<CharacterModel>()

    override fun onCreateView(
        inflater: LayoutInflater, container: ViewGroup?,
        savedInstanceState: Bundle?
    ): View? {
        binding = FragmentFirstBinding.inflate(layoutInflater)
        return binding.root

    }

    override fun onViewCreated(view: View, savedInstanceState: Bundle?) {
        super.onViewCreated(view, savedInstanceState)

        loadData()
        val adapter = CharacterViewHolder(list, this:: onClick)
        binding.recycleView.adapter =adapter


    }

    private fun loadData() {
        list.add(CharacterModel("data:image/jpeg;base64,", "Dead", "Ворд из холодной долины"))
        list.add(CharacterModel("data:image/jpeg;base64,", "Dead", "Судия Гундир"))
        list.add(CharacterModel("data:image/jpeg;base64,", "Dead", "Хранитель Бездны"))
        list.add(CharacterModel("data:image/jpeg;base64,", "Dead", "Гигант Йорм"))
    }

   private  fun onClick(position: Int){
       
            findNavController().navigate(FirstFragmentDirections.actionFirstFragmentToSecondFragment(R.id.tx_status, R.id.character_name))



        }
    }




